        import React, { Component } from 'react';
        import './Data.css'

        import { connect } from 'react-redux';
        import { deleteUser } from './dataActions';
        import withLogger from '../HOC/HOC';
        class Data extends Component {
            constructor(props) {
                super(props);
               // this.updateData = this.updateData.bind(this);
            }
            state = {
                users: [],
                error: null, // Добавляем состояние для хранения ошибки
                updMail: "",
                updPassword: "",
                updName: "",
                updSurname: "",
                updAge: 0,
                updAbout: "",
                updGender_id: 0,
                updUser_id : 0
            };

            componentDidMount() {
                this.fetchData();
            }

            handleChange = (e) => {
                const { name, value } = e.target;
                this.setState((prevState) => ({
                    ...prevState,
                    [name]:
                        name === 'updAge'
                            ? value === ''
                                ? ''
                                : +value
                            : name === 'updGender_id'
                                ? +value // Convert the value to a number
                                : value,
                }));
            };

            fetchData = async () => {
                try {
                    const response = await fetch("http://localhost:8080/api/users");
                    const data = await response.json();
                    this.setState({ users: data, error: null }); // Обнуляем ошибку при успешном запросе
                    this.setState({updMail : data[0].mail, updPassword : data[0].password,
                        updName : data[0].name, updSurname : data[0].surname, updAge : data[0].age, updAbout: data[0].about, updGender_id : data[0].gender.id,
                        updUser_id : data[0].id});
                } catch (error) {
                    this.setState({ error }); // Сохраняем ошибку в состоянии
                }
            };

            deleteData(id) {
                this.props.deleteUser(id);
                fetch(`http://localhost:8080/api/delete-user/${id}`, {
                    method: 'DELETE'
                }).then((response) => response.json())
            }
            selectUser(id){
                const selectedUser = this.state.users.find(user => user.id === id);
                this.setState({updMail : selectedUser.mail, updPassword : selectedUser.password,
                    updName : selectedUser.name, updSurname : selectedUser.surname, updAge : selectedUser.age, updAbout: selectedUser.about, updGender_id : selectedUser.gender.gender,
                    updUser_id : selectedUser.id});
            }
            updateData = () => {
                let mail = this.state.updMail
                let password = this.state.updPassword
                let name = this.state.updName
                let surname = this.state.updSurname
                let age = this.state.updAge
                let about = this.state.updAbout
                let gender_id = this.state.updGender_id
                let updateThisUser = this.state.updUser_id
                console.log(updateThisUser)
                let newData =  {mail, password, name, surname, age, about, gender_id}
                console.log(newData)
                fetch(`http://localhost:8080/api/update-user/${updateThisUser}`, {
                    method: 'PUT',
                    headers: { "content-type": "application/json" },
                    body: JSON.stringify(newData)
                }).then((response) => {
                    response.json().then((resp) => {
                        this.fetchData()
                    })
                })
            }
            render() {
                const { error, users, updMail, updPassword, updName, updSurname, updAge, updAbout, updGender_id } = this.state;
                if (error) {
                    throw error; // Выбрасываем ошибку, чтобы ErrorBoundary мог ее обработать
                }
                return (
                    <div className="dataContainer">
                        <h2>Users</h2>
                        <table class="table table-dark">
                            <thead>
                            <tr>
                                <th>Mail</th>
                                <th>Password</th>
                                <th>Name</th>
                                <th>Surname</th>
                                <th>Age</th>
                                <th>Gender</th>
                                <th>Role</th>
                                <th>RC</th>
                                <th>About</th>
                                <th>Delete</th>
                                <th>Update</th>
                            </tr>
                            </thead>
                            <tbody>
                            {users.map(user => (
                                <tr key={user.id}>
                                    <td>{user.mail}</td>
                                    <td>{user.password}</td>
                                    <td>{user.name}</td>
                                    <td>{user.surname}</td>
                                    <td>{user.age}</td>
                                    <td>{user.gender.gender}</td>
                                    <td>{user.rolesList[0].role}</td>
                                    <td>{user.regularCustomer.id}</td>
                                    <td>{user.about}</td>
                                    <td><button onClick={()=>this.deleteData(user.id)} className="btn btn-danger">Delete</button></td>
                                    <td><button onClick={()=>this.selectUser(user.id)} className="btn btn-light">Update</button></td>
                                </tr>
                            ))}
                            </tbody>
                        </table>

                        <div className="container-sm">
                                <div className="card">
                                    <div className="card-header">
                                        <h3>Updating User</h3>
                                    </div>
                                    <div className="card-body">
                                        <div className="row">

                                            <div className="form-group">
                                                <label>Mail</label>
                                                <input name="updMail" value={updMail} onChange={this.handleChange} className="form-control"></input>
                                            </div>


                                            <div className="form-group">
                                                <label>Password </label>
                                                <input name="updPassword" value={updPassword} onChange={this.handleChange} type="" className="form-control"></input>
                                            </div>

                                            <div className="form-group">
                                                <label>Name</label>
                                                <input name="updName" value={updName} onChange={this.handleChange} className="form-control"></input>
                                            </div>
                                            <div className="form-group">
                                                <label>Surname</label>
                                                <input name="updSurname" value={updSurname} onChange={this.handleChange} className="form-control"></input>
                                            </div>
                                            <div className="form-group">
                                                <label>Age</label>
                                                <input name="updAge" value={updAge} onChange={this.handleChange} type="numeric" className="form-control"></input>
                                            </div>
                                            <div className="form-group">
                                                <label>About</label>
                                                <input name="updAbout" value={updAbout} onChange={this.handleChange} className="form-control"></input>
                                            </div>

                                            <div className="form-group">
                                                <label>Gender</label>
                                                <br></br>
                                                <input
                                                    className="form-check"
                                                    type="checkbox"
                                                    checked={updGender_id === 1} // Check against the numeric values
                                                    onChange={(e) =>
                                                        this.handleChange({ target: { name: 'updGender_id', value: 1 } })
                                                    }
                                                    name="updGender_id"
                                                    value="1"
                                                />
                                                <label>Male</label>
                                                <input
                                                    className="form-check"
                                                    type="checkbox"
                                                    checked={updGender_id === 2} // Check against the numeric values
                                                    onChange={(e) =>
                                                        this.handleChange({ target: { name: 'updGender_id', value: 2 } })
                                                    }
                                                    name="updGender_id"
                                                    value="2"
                                                />
                                                <label>Female</label>

                                            </div>

                                        </div>

                                    </div>
                                    <div className="card-footer">
                                        <button onClick={this.updateData} type="submit" className="btn btn-secondary">Update</button>
                                    </div>
                                </div>
                        </div>
                    </div>
                );
            }
        }

        const mapDispatchToProps = {
            deleteUser,
        };

        export default connect(null, mapDispatchToProps)(withLogger(Data));
