export const fetchUsers = () => {
    return async (dispatch) => {
        try {
            const response = await fetch('http://localhost:8080/api/users');
            const data = await response.json();
            dispatch({
                type: 'FETCH_USERS_SUCCESS',
                payload: data
            });
        } catch (error) {
            dispatch({
                type: 'FETCH_USERS_ERROR',
                payload: error
            });
        }
    };
};

export const deleteUser = (id) => {
    return async (dispatch) => {
        try {
            await fetch(`http://localhost:8080/api/delete-user/${id}`, {
                method: 'DELETE'
            });
            dispatch({
                type: 'DELETE_USER',
                payload: id
            });
        } catch (error) {
            console.error(error);
        }
    };
};

export const selectUser = (id) => {
    return {
        type: 'SELECT_USER',
        payload: id
    };
};

export const updateUser = (id, data) => {
    return async (dispatch) => {
        try {
            await fetch(`http://localhost:8080/api/update-user/${id}`, {
                method: 'PUT',
                headers: { 'content-type': 'application/json' },
                body: JSON.stringify(data)
            });
            dispatch({
                type: 'UPDATE_USER',
                payload: data
            });
        } catch (error) {
            console.error(error);
        }
    };
};
