// dataActions.js

export const deleteUser = (userId) => {
    return (dispatch) => {
        // Perform the deletion operation (e.g., make an API request to delete the user)
        // Once the deletion is successful, dispatch the action with the deleted user's ID
        // You can replace the following code with your actual deletion logic
        // Example:
        deleteUserFromAPI(userId)
            .then(() => {
                dispatch({
                    type: 'DELETE_USER',
                    payload: userId,
                });
            })
            .catch((error) => {
                // Handle any error that occurred during deletion
                console.error('Failed to delete user:', error);
            });
    };
};

// Helper function to simulate API deletion request
const deleteUserFromAPI = (userId) => {
    return new Promise((resolve, reject) => {
        // Simulating a successful deletion after a short delay
        setTimeout(() => {
            resolve();
        }, 1000);
    });
};
