const initialState = {
    users: [],
    error: null,
    updMail: '',
    updPassword: '',
    updName: '',
    updSurname: '',
    updAge: 0,
    updAbout: '',
    updGender_id: 0,
    updUser_id: 0
};

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'FETCH_USERS_SUCCESS':
            return {
                ...state,
                users: action.payload,
                error: null
            };
        case 'FETCH_USERS_ERROR':
            return {
                ...state,
                error: action.payload
            };
        case 'DELETE_USER':
            return {
                ...state,
                users: state.users.filter(user => user.id !== action.payload)
            };
        case 'SELECT_USER':
            const selectedUser = state.users.find(user => user.id === action.payload);
            return {
                ...state,
                updMail: selectedUser.mail,
                updPassword: selectedUser.password,
                updName: selectedUser.name,
                updSurname: selectedUser.surname,
                updAge: selectedUser.age,
                updAbout: selectedUser.about,
                updGender_id: selectedUser.gender.gender,
                updUser_id: selectedUser.id
            };
        case 'UPDATE_USER':
            return {
                ...state,
                updMail: action.payload.mail,
                updPassword: action.payload.password,
                updName: action.payload.name,
                updSurname: action.payload.surname,
                updAge: action.payload.age,
                updAbout: action.payload.about,
                updGender_id: action.payload.gender_id
            };
        default:
            return state;
    }
};

export default rootReducer;
