import React, { Component } from 'react';

class SearchAndFilter extends Component {
    constructor(props) {
        super(props);
        this.state = {
            searchQuery: '',
            filteredUsers: [],
        };
    }

    handleSearchInputChange = (event) => {
        const searchQuery = event.target.value;
        this.setState({ searchQuery }, () => {
            this.filterUsers();
        });
    };

    filterUsers = () => {
        const { searchQuery } = this.state;
        const { users } = this.props;

        // Фильтрация пользователей по введенному значению
        const filteredUsers = users.filter((user) => {
            return (
                user.mail.includes(searchQuery) ||
                user.password.includes(searchQuery) ||
                user.name.includes(searchQuery) ||
                user.surname.includes(searchQuery) ||
                user.age.toString().includes(searchQuery) ||
                user.gender.gender.includes(searchQuery) ||
                user.rolesList[0].role.includes(searchQuery) ||
                user.regularCustomer.id.toString().includes(searchQuery) ||
                user.about.includes(searchQuery)
            );
        });

        this.setState({ filteredUsers });
    };

    render() {
        const { filteredUsers } = this.state;

        return (
            <div>
                <input
                    type="text"
                    placeholder="Search..."
                    onChange={this.handleSearchInputChange}
                />

                <table className="table table-dark">
                    <thead>
                    <tr>
                        <th>Mail</th>
                        <th>Password</th>
                        <th>Name</th>
                        <th>Surname</th>
                        <th>Age</th>
                        <th>Gender</th>
                        <th>Role</th>
                        <th>Reg. Customer</th>
                        <th>About</th>
                        <th>Delete</th>
                        <th>Update</th>
                    </tr>
                    </thead>
                    <tbody>
                    {filteredUsers.map((user) => (
                        <tr key={user.id}>
                            <td>{user.mail}</td>
                            <td>{user.password}</td>
                            <td>{user.name}</td>
                            <td>{user.surname}</td>
                            <td>{user.age}</td>
                            <td>{user.gender.gender}</td>
                            <td>{user.rolesList[0].role}</td>
                            <td>{user.regularCustomer.id}</td>
                            <td>{user.about}</td>
                            <td>
                                <button
                                    onClick={() => this.deleteData(user.id)}
                                    className="btn btn-danger"
                                >
                                    Delete
                                </button>
                            </td>
                            <td>
                                <button
                                    onClick={() => this.selectUser(user.id)}
                                    className="btn btn-light"
                                >
                                    Update
                                </button>
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
        );
    }
}

export default SearchAndFilter;
