import React, { Component } from 'react';

class Data extends Component {
    state = {
        users: [],
        error: null, // Добавляем состояние для хранения ошибки
    };

    componentDidMount() {
        this.fetchData();
    }

    fetchData = async () => {
        try {
            const response = await fetch("http://localhost:8080/api/users");
            const data = await response.json();
            this.setState({ users: data, error: null }); // Обнуляем ошибку при успешном запросе
        } catch (error) {
            this.setState({ error }); // Сохраняем ошибку в состоянии
        }
    };

    deleteData(id) {
        fetch(`http://localhost:8080/api/delete-user/${id}`, {
            method: 'DELETE'
        }).then((response) => response.json())
    }

    render() {
        const { error, users } = this.state;
        if (error) {
            throw error; // Выбрасываем ошибку, чтобы ErrorBoundary мог ее обработать
        }
        return (
            <div>
                <table class="table table-dark">
                    <thead>
                    <tr>
                        <th>Mail</th>
                        <th>Password</th>
                        <th>Name</th>
                        <th>Surname</th>
                        <th>Gender</th>
                        <th>Role</th>
                        <th>Reg. Customer</th>
                        <th>About</th>
                        <th>Delete/Edit</th>
                    </tr>
                    </thead>
                    <tbody>
                    {users.map(user => (
                        <tr key={user.id}>
                            <td>{user.mail}</td>
                            <td>{user.password}</td>
                            <td>{user.name}</td>
                            <td>{user.surname}</td>
                            <td>{user.gender.gender}</td>
                            <td>{user.rolesList[0].role}</td>
                            <td>{user.regularCustomer.id}</td>
                            <td>{user.about}</td>
                            <td><button onClick={()=>this.deleteData(user.id)} className="btn btn-danger">Delete</button>
                               <button onClick={()=>this.selecrUser(user.id)} className="btn btn-light">Update</button></td>
                        </tr>
                    ))}
                    </tbody>
                </table>

                <div>

                </div>
            </div>
        );
    }
}

export default Data;
