import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import {
    fetchUsers,
    deleteUser,
    selectUser,
    updateUser
} from './actions';

const Data = ({
                  users,
                  error,
                  updMail,
                  updPassword,
                  updName,
                  updSurname,
                  updAge,
                  updAbout,
                  updGender_id,
                  updUser_id,
                  fetchData,
                  deleteData,
                  selectUserData,
                  updateData
              }) => {
    useEffect(() => {
        fetchData();
    }, [fetchData]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        const parsedValue =
            name === 'updAge' || name === 'updGender_id' ? parseInt(value) : value;
        selectUserData({ [name]: parsedValue });
    };

    const handleDelete = (id) => {
        deleteData(id);
    };

    const handleUpdate = () => {
        const newData = {
            mail: updMail,
            password: updPassword,
            name: updName,
            surname: updSurname,
            age: updAge,
            about: updAbout,
            gender_id: updGender_id
        };
        updateData(updUser_id, newData);
    };

    if (error) {
        throw error;
    }

    return (
        <div className="dataContainer">
            <h2>Users</h2>
            <table className="table table-dark">
                {/* Table content */}
            </table>

            <div className="container-sm">
                <div className="card">
                    {/* Update form */}
                </div>
            </div>
        </div>
    );
};

const mapStateToProps = (state) => ({
    users: state.users,
    error: state.error,
    updMail: state.updMail,
    updPassword: state.updPassword,
    updName: state.updName,
    updSurname: state.updSurname,
    updAge: state.updAge,
    updAbout: state.updAbout,
    updGender_id: state.updGender_id,
    updUser_id: state.updUser_id
});

const mapDispatchToProps = (dispatch) => ({
    fetchData: () => dispatch(fetchUsers()),
    deleteData: (id) => dispatch(deleteUser(id)),
    selectUserData: (id) => dispatch(selectUser(id)),
    updateData: (id, data) => dispatch(updateUser(id, data))
});

export default connect(mapStateToProps, mapDispatchToProps)(Data);
