import React, { Component } from 'react';
import {Link} from "react-router-dom";

class Data extends Component {
    state = {
        users: [],
        error: null, // Добавляем состояние для хранения ошибки
        mail: "",
        password: "",
        name: "",
        surname: "",
        age: 0,
        about: "",
        gender_id: "",
        user_id : 0
    };

    componentDidMount() {
        this.fetchData();
    }

    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    };

    fetchData = async () => {
        try {
            const response = await fetch("http://localhost:8080/api/users");
            const data = await response.json();
            this.setState({ users: data, error: null }); // Обнуляем ошибку при успешном запросе
            this.setState({mail : data[0].mail, password : data[0].password,
            name : data[0].name, surname : data[0].surname, age : data[0].age, about: data[0].about, gender_id : data[0].gender.gender,
            user_id : data[0].user_id});
        } catch (error) {
            this.setState({ error }); // Сохраняем ошибку в состоянии
        }
    };

    deleteData(id) {
        fetch(`http://localhost:8080/api/delete-user/${id}`, {
            method: 'DELETE'
        }).then((response) => response.json())
    }

    render() {
        const { error, users, mail, password, name, surname, age, about, gender_id } = this.state;
        if (error) {
            throw error; // Выбрасываем ошибку, чтобы ErrorBoundary мог ее обработать
        }
        return (
            <div>
                <table class="table table-dark">
                    <thead>
                    <tr>
                        <th>Mail</th>
                        <th>Password</th>
                        <th>Name</th>
                        <th>Surname</th>
                        <th>Gender</th>
                        <th>Role</th>
                        <th>Reg. Customer</th>
                        <th>About</th>
                        <th>Delete</th>
                        <th>Update</th>
                    </tr>
                    </thead>
                    <tbody>
                    {users.map(user => (
                        <tr key={user.id}>
                            <td>{user.mail}</td>
                            <td>{user.password}</td>
                            <td>{user.name}</td>
                            <td>{user.surname}</td>
                            <td>{user.gender.gender}</td>
                            <td>{user.rolesList[0].role}</td>
                            <td>{user.regularCustomer.id}</td>
                            <td>{user.about}</td>
                            <td><button onClick={()=>this.deleteData(user.id)} className="btn btn-danger">Delete</button></td>
                            <td><button onClick={()=>this.selectUser(user.id)} className="btn btn-light">Update</button></td>
                        </tr>
                    ))}
                    </tbody>
                </table>

                <div>
                    <form className="container" onSubmit={submit}>
                        <div className="card">
                            <div className="card-header">
                                <h3>Add User</h3>
                            </div>
                            <div className="card-body">
                                <div className="row">

                                    <div className="form-group">
                                        <label>Mail</label>
                                        <input value={mail} onChange={this.handleChange} className="form-control"></input>
                                    </div>


                                    <div className="form-group">
                                        <label>Password </label>
                                        <input value={password} onChange={this.handleChange} type="password" className="form-control"></input>
                                    </div>


                                    <div className="form-group">
                                        <label>Name</label>
                                        <input value={name} onChange={this.handleChange} className="form-control"></input>
                                    </div>
                                    <div className="form-group">
                                        <label>Surname</label>
                                        <input value={surname} onChange={this.handleChange} className="form-control"></input>
                                    </div>
                                    <div className="form-group">
                                        <label>Age</label>
                                        <input value={age} onChange={this.handleChange} type="numeric" className="form-control"></input>
                                    </div>
                                    <div className="form-group">
                                        <label>About</label>
                                        <input value={about} onChange={this.handleChange} className="form-control"></input>
                                    </div>

                                    <div className="form-group">
                                        <label>Gender</label>
                                        <br></br>
                                        <input className="form-check" type="checkbox" checked={gender_id === 'male'} onChange={this.handleChange} name="gender_id" value="male"></input>
                                        <label>Male</label>
                                        <input className="form-check" type="checkbox" checked={gender_id === 'female'} onChange={this.handleChange} name="gender_id" value="female"></input>
                                        <label>Female</label>

                                    </div>

                                </div>

                            </div>
                            <div className="card-footer">
                                <button type="submit" className="btn btn-secondary">Update</button>

                            </div>
                        </div>
                    </form>
                </div>
            </div>
        );
    }
}

export default Data;
